import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import './App.css';
import Home from './pages/Home';
import List from './pages/List';
import Users from './pages/Users';
import Contact from './pages/Contact';
import LatestBlog from './pages/latestBlog';
import Navbar from './components/Navbar/Navbar';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      userType: null,
      userAuth: false
    }
  }

  signInHandler = async(userName, password) => {
    try {
      await fetch(`/sign-in/${userName}/${password}`, {
        method: 'GET',
        headers: {"Content-Type": "application/json"}
      })
      .then(res => res.json())
      .then(data => this.setState({
        user: data[0].first_name,
        userType: data[0].userType,
        userAuth: true
      }));
    } catch (err) {
      console.log(err);
    }
  }

  render() {
    const App = () => (
      <div>
        <Navbar userName={this.state.user} />
        <Switch>
          <Route exact path='/' render={() => <Home user={this.state.user}/>}/>
          <Route path='/list' component={List}/>
          <Route path='/users' render={() => <Users auth={this.state.userAuth} click={(user, pass) => this.signInHandler(user, pass)} />}/>
          <Route path='/contact' component={Contact}/>
          <Route path='/latestBlog' component={LatestBlog}/>
        </Switch>
      </div>
    )
    return (
      <Switch>
        <App/>
      </Switch>
    );
  }
}

export default App;
