import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Home extends Component {
    saveCat = (name, image) => {
        fetch('/api/createCampground', {
            method: 'post',
            body: JSON.stringify({
                name: name,
                image: image
            })
        });
    }
    render() {
        return (
            <div className="App">
                <h1>Project Home</h1>
                <Link to={'./list'}>
                    <button variant="raised">
                        My List
                    </button>
                </Link>
                <Link to={'./list'}>
                    <button onClick={() => this.saveCat("Bass Creek", "BassCreekImage")} variant="raised">
                        saveCat
                    </button>
                </Link>
                <Link to={'./users'}>
                    <button variant="raised">
                        My User
                    </button>
                </Link>
                <Link to={'./Contact'}>
                    <button variant="raised">
                        Contact Me
                    </button>
                </Link>
            </div>
        )
    }
}

export default Home;
