import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import './App.css';
import Home from './pages/Home';
import List from './pages/List';
import Users from './pages/Users';
import Contact from './pages/Contact';
import LatestBlog from './pages/latestBlog';
import Navbar from './components/Navbar/Navbar';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      userType: null
    }
  }
  componentDidUpdate(prevState) {
    if (this.state.user !== prevState.user) {
      console.log('changed', this.state.user);

    }
    if (this.state.userType === 'Admin') {
      console.log('admin set');
    }
    console.log(this.state);

    console.log('triggered');

  }
  signInHandler = (userName, password) => {
    fetch(`/sign-in/${userName}/${password}`, {
      method: 'GET',
      headers: {"Content-Type": "application/json"}
    })
    .then(res => res.json())
    .then(data => this.setState({
      user: data.first_name,
      userType: data.userType
    }));
  }

  render() {
    const App = () => (
      <div>
        <Navbar userName={this.state.user}/>
        <Switch>
          <Route exact path='/' component={Home}/>
          <Route path='/list' component={List}/>
          <Route path='/users' render={() => <Users click={(user, pass) => this.signInHandler(user, pass)} />}/>
          <Route path='/contact' component={Contact}/>
          <Route path='/latestBlog' component={LatestBlog}/>
        </Switch>
      </div>
    )
    return (
      <Switch>
        <App/>
      </Switch>
    );
  }
}

export default App;
