import React, { Component } from 'react';

class Home extends Component {
    constructor(props){
        super(props);
        this.user = props.userName;
        this.display = '';
    }

    componentDidUpdate(prevProps) {
        if (prevProps.username !== this.user) {
            this.renderName();
        }
    }
    renderName = () => {
        if (this.user !== null) {
            this.display = <h2>Welcome {this.user}</h2>;
        }
    }
    render() {
        return (
            <div className="App">
                <h1>Project Home</h1>
                {/* {this.display} */}
            </div>
        )
    }
}

export default Home;
